#!/bin/bash
version=$(bin/neo4j-admin --version | cut -d '.' -f 1)
if [[ $version -ge 5 ]]; then
	bin/neo4j-admin database import full neo4j --delimiter="\t" --array-delimiter="|" --quote='"' --overwrite-destination=true --skip-bad-relationships=true --skip-duplicate-nodes=true --nodes="neo4j-admin-import-call/Drug-header.csv,neo4j-admin-import-call/Drug-part.*" --nodes="neo4j-admin-import-call/Gene-header.csv,neo4j-admin-import-call/Gene-part.*" --nodes="neo4j-admin-import-call/CellularComponent-header.csv,neo4j-admin-import-call/CellularComponent-part.*" --nodes="neo4j-admin-import-call/MolecularActivity-header.csv,neo4j-admin-import-call/MolecularActivity-part.*" --nodes="neo4j-admin-import-call/BiologicalProcess-header.csv,neo4j-admin-import-call/BiologicalProcess-part.*" --nodes="neo4j-admin-import-call/Pathway-header.csv,neo4j-admin-import-call/Pathway-part.*" --relationships="neo4j-admin-import-call/MacromolecularMachineToMolecularActivityAssociation-header.csv,neo4j-admin-import-call/MacromolecularMachineToMolecularActivityAssociation-part.*" --relationships="neo4j-admin-import-call/MacromolecularMachineToCellularComponentAssociation-header.csv,neo4j-admin-import-call/MacromolecularMachineToCellularComponentAssociation-part.*" --relationships="neo4j-admin-import-call/PathwayGeneInteraction-header.csv,neo4j-admin-import-call/PathwayGeneInteraction-part.*" --relationships="neo4j-admin-import-call/DrugToGeneAssociation-header.csv,neo4j-admin-import-call/DrugToGeneAssociation-part.*" 
else
	bin/neo4j-admin import --database=neo4j --delimiter="\t" --array-delimiter="|" --quote='"' --force=true --skip-bad-relationships=true --skip-duplicate-nodes=true --nodes="neo4j-admin-import-call/Drug-header.csv,neo4j-admin-import-call/Drug-part.*" --nodes="neo4j-admin-import-call/Gene-header.csv,neo4j-admin-import-call/Gene-part.*" --nodes="neo4j-admin-import-call/CellularComponent-header.csv,neo4j-admin-import-call/CellularComponent-part.*" --nodes="neo4j-admin-import-call/MolecularActivity-header.csv,neo4j-admin-import-call/MolecularActivity-part.*" --nodes="neo4j-admin-import-call/BiologicalProcess-header.csv,neo4j-admin-import-call/BiologicalProcess-part.*" --nodes="neo4j-admin-import-call/Pathway-header.csv,neo4j-admin-import-call/Pathway-part.*" --relationships="neo4j-admin-import-call/MacromolecularMachineToMolecularActivityAssociation-header.csv,neo4j-admin-import-call/MacromolecularMachineToMolecularActivityAssociation-part.*" --relationships="neo4j-admin-import-call/MacromolecularMachineToCellularComponentAssociation-header.csv,neo4j-admin-import-call/MacromolecularMachineToCellularComponentAssociation-part.*" --relationships="neo4j-admin-import-call/PathwayGeneInteraction-header.csv,neo4j-admin-import-call/PathwayGeneInteraction-part.*" --relationships="neo4j-admin-import-call/DrugToGeneAssociation-header.csv,neo4j-admin-import-call/DrugToGeneAssociation-part.*" 
fi